using UnityEditor;
using UnityEngine;

public static class SkinX {
    private const string LOCK_DARK_ACTIVE =
        "";

    private const string LOCK_DARK_INACTIVE =
        "";

    private const string LOCK_LIGHT_ACTIVE =
        "";

    private const string LOCK_LIGHT_INACTIVE =
        "";

    private const string EYE_DARK_ACTIVE =
        "";

    private const string EYE_DARK_INACTIVE =
        "";

    private const string EYE_LIGHT_ACTIVE =
        "";

    private const string EYE_LIGHT_INACTIVE =
        "";

    private const string STATIC_DARK_ACTIVE =
        "";

    private const string STATIC_DARK_INACTIVE =
        "";

    private const string STATIC_LIGHT_ACTIVE =
        "";

    private const string STATIC_LIGHT_INACTIVE =
        "";

    private static int GetIndex(bool active) {
        // 0 - active pro
        // 1 - inactive pro
        // 2 - active indie
        // 3 - inactive indie
        return (active ? 0 : 1) + ((EditorGUIUtility.isProSkin ? 0 : 1) << 1);
    }

    private static Texture2D GetTexture(int index, string id, params string[] base64) {
        return base64[index].xToTexture2D(id);
    }

    public static Texture2D IcoLock(bool active) {
        var idx = GetIndex(active);
        var id = "lock" + idx;

        return TextureX.HasTextureId(id)
            ? id.xGetTextureFromId()
            : GetTexture(idx, id, LOCK_DARK_ACTIVE, LOCK_DARK_INACTIVE, LOCK_LIGHT_ACTIVE, LOCK_LIGHT_INACTIVE);
    }

    public static Texture2D IcoStatic(bool active) {
        var idx = GetIndex(active);
        var id = "static" + idx;

        return TextureX.HasTextureId(id)
            ? id.xGetTextureFromId()
            : GetTexture(idx, id, STATIC_DARK_ACTIVE, STATIC_DARK_INACTIVE, STATIC_LIGHT_ACTIVE, STATIC_LIGHT_INACTIVE);
    }

    public static Texture2D IcoEye(bool active) {
        var idx = GetIndex(active);
        var id = "eye" + idx;

        return TextureX.HasTextureId(id)
            ? id.xGetTextureFromId()
            : GetTexture(idx, id, EYE_DARK_ACTIVE, EYE_DARK_INACTIVE, EYE_LIGHT_ACTIVE, EYE_LIGHT_INACTIVE);
    }
}